import React from 'react'

export default function Home() {
    const timezone = Intl.DateTimeFormat().resolvedOptions().timeZone;

    const weekday = ["Sunday","Monday","Tuesday","Wednesday","Thursday","Friday","Saturday"];
    const d = new Date();
    const day = weekday[d.getDay()];

    const today = new Date();

    const time = today.getHours() + ":" + today.getMinutes() + ":" + today.getSeconds();
    const date = today.getDate()+'-'+(today.getMonth()+1)+'-'+ today.getFullYear()

    const myHTML = 
    `<div id="wcom-bf29c46e4fa38dabca05512d5720f4be" class="wcom-default w300x250"  style="border: 1px solid #CCC; background-color: #FCFCFC; border-radius: 5px">
    <link rel="stylesheet" href="//cs3.wettercomassets.com/woys/5/css/w.css" media="all">
    <div class="wcom-city">
        <a style="color: #000" href="https://www.wetter.com/deutschland/berlin/DE0001020.html" target="_blank" rel="nofollow" aria-label="Wetter Berlin" title="Wetter Berlin">Wetter Berlin ‚òÄ ‚òÅ ‚òî</a>
    </div>

    <div id="wcom-bf29c46e4fa38dabca05512d5720f4be-weather"></div>
        <script type="text/javascript" src="//cs3.wettercomassets.com/woys/5/js/w.js">
        </script>
        <script type="text/javascript">_wcomWidget({id: 'wcom-bf29c46e4fa38dabca05512d5720f4be',location: 'DE0001020',format: '300x250',type: 'summary'});
        </script>
    </div>`;



  return (

        <div className='home-items'>
            <img className ="card" src='/World-Flag-map.jpg' alt='map'/>
            <br/>
            <div className ="card">
                <div className ="card-header ">‚è∞ Time Now : <b className='border p-1 text-primary'>{time}</b>  <br/>Your current TimeZone : <b className='p-1 text-primary'>{timezone}</b></div>
                    <div className="card-body">
                        <h5 className="card-title">Weather Now :</h5>
                        <p className="card-text weather">
                        <div className="content" dangerouslySetInnerHTML={{__html: myHTML}}></div>
                        </p>

                    </div>
                <div className="card-footer bg-transparent">üìÖ Today is : <b className='p-1 text-primary'>{day} , {date}</b></div>
            </div>
        </div>
    )
}
