import React, { useContext } from "react";
import Country from "./Country";
import {Link} from 'react-router-dom'
import { ThemeContext } from "../context/ThemeContext";


const CountriesGrid = ({name , flag})=>{
    const {landLayout} = useContext(ThemeContext);

    const { isVisited , noVisit , visited } = landLayout;
    const layout = isVisited ? visited : noVisit ;


    return(
        <div className="lands-card card" 
                     style={{color: layout.text, background: layout.hintergrund , filter: layout.filter}}>

            <Link to={'/all_countries/'+name} > 
                    <div className="card-img">
                    <img className="card-img-top" src={flag} alt='land-flag' style={{maxWidth:'100%',maxHeight:'100%'}}/>
                    </div>
            </Link>
                    <div class="card-body" style= {{position:'relative' , button: 0}}>
                    <h3 className="card-title text-danger ">{'üìç '+name}</h3> 


                    <div class="card-footer bg-transparent">

                    <button className='btn btn-outline-success m-1'
                    onClick={()=>{
                        localStorage.setItem(`save_${name}` , name)
                    }} 
                    >Visited</button>

                    <button className='btn btn-outline-secondary m-1'
                    onClick={e=>e.preventDefault()}
                    >want to visit</button>

                    </div>
                </div>
            </div>

        )

}

export default CountriesGrid
