import React from "react";
import {Link} from 'react-router-dom'
import ContiFunction from './ContiFunction';

const Continents = ({countries})=>{
    let continents = [
        {   name : 'Africa' , 
            img : './imges/africa.jpg', 
            path: 'Africa'
        },
        {   name : 'The Americas' , 
            img : './imges/north-america.jpg', 
            path: 'Americas'
        },
        {   name : 'Asia' , 
            img : './imges/asia.jpg', 
            path: 'Asia'
        },
        {   name : 'Europe' , 
            img : './imges/Europe.jpg', 
            path: 'Europe'
        },
        {   name : 'Australia' , 
            img : './imges/Australia.jpg', 
            path: 'Australia'
        },
        {   name : 'Antarctica - Oceania' , 
            img : './imges/Antarctica.jpg', 
            path: 'Antarctica'
        } ]

        function sortLand ({countries}, continents ){
            let sorted = countries.filter(land=>{
                            continents.filter(con=>{
                                con.path === land.region
                                console.log(sorted);
                                return sorted
                            })
                       })
        }


    return(
        <div className="continents ">
            <div className="cont-card" >
                {continents.map(con=>{
                                return(
                                    <div className="card"

                                    onClick={()=>{
                                        let sorted = countries.filter(land=>land.region === con.path)
                                        console.log(sorted);
                                        return sorted
                                    }}>
                                        <Link to= {'/'+ con.path}><h3>{con.name}</h3></Link>

                                        <img src={con.img} alt='map' width='200px'/>
                                    </div>
                                )}
                            )
                }

            </div>
        </div>
    )
}

export default Continents

