    const d = new Date();
    const day = weekday[d.getDay()];

    const today = new Date();

    const time = today.getHours() + ":" + today.getMinutes() + ":" + today.getSeconds();
    const date = today.getDate()+'-'+(today.getMonth()+1)+'-'+ today.getFullYear()



  return (

        <div id='home-items'>
            <img  src='/World-Flag-map.jpg' alt='map'/>
            <br/><br/>
            <div className ="card">
                <div className ="card-header bg-transparent">‚è∞ Time Now : {time} <br/>Your current TimeZone : {timezone}</div>
                    <div class="card-body">
                        <h5 class="card-title">Weather Now :</h5>
                        <p class="card-text">‚òÄ ‚òÅ ‚òî</p>
                    </div>
                <div class="card-footer bg-transparent">üìÖ Today is : {day} , {date}</div>
            </div>
        </div>
    )
}
