import './App.css';
import React , {useState , useEffect}from "react";
import Navigation from './component/Navigation';
import CountriesGrid from './component/CountriesGrid';
import {BrowserRouter as Router, Routes, Route} from "react-router-dom";


function App() {
    const [countries , setCountries] = useState([])
    let url = 'https://restcountries.com/v2/all';

    useEffect(()=>{
      const fetchData = async ()=>{
        try{
          const response = await fetch (url);
          const data = await response.json();
          console.log(data);

          if(data) setCountries(data)
            if(countries)console.log('countries',countries);
          }catch (error){
          console.log("error", error);
        }
      }
      fetchData()
    } , [])

      return (
    <Router>
      <div className="App container p-3 my-3">
        <h1 className='mb-4 text-secondary'>Countries and Regions in the World</h1>
          <Navigation countries={countries.length>0 ? countries : null}/> 
        <img src='/World-Flag-map.jpg' alt='map' width='80%'/>
        <br/>
        <Routes>
            <Route path = '/' 
                   element = {'home'}/>

            <Route path = '/all_countries' 
                   element = {
                   <CountriesGrid 
                              countries={countries.length>0 ? countries : null}/>}/>
        </Routes>
      </div>
    </Router>
  );
}

export default App;

