import React from "react";
import {Link} from 'react-router-dom'
i

const Continents = ({countries})=>{
    const [sorted , setSorted] = useState([])
    let continents = [
        {   name : 'Africa' , 
            img : './imges/africa.jpg', 
            path: 'Africa'
        },
        {   name : 'The Americas' , 
            img : './imges/north-america.jpg', 
            path: 'Americas'
        },
        {   name : 'Asia' , 
            img : './imges/asia.jpg', 
            path: 'Asia'
        },
        {   name : 'Europe' , 
            img : './imges/Europe.jpg', 
            path: 'Europe'
        },
        {   name : 'Australia' , 
            img : './imges/Australia.jpg', 
            path: 'Australia'
        },
        {   name : 'Antarctica - Oceania' , 
            img : './imges/Antarctica.jpg', 
            path: 'Antarctica'
        } ]




    return(
        <div className="continents ">
            <div className="cont-card" >
                {continents.map(con=>{
                                return(
                                    <div className="card"

                                    onClick={()=>{
                                        sorted = countries.filter(land=>land.region === con.path)   
                                        console.log('sorted',sorted);
                                        return sorted
                                    }}>
                                        <Link to= {'/'+ con.path}><h3>{con.name}</h3></Link>

                                        <img src={con.img} alt='map' width='200px'/>
                                    </div>
                                )}
                            )
                }

            </div>
            <div className="sortedLands">
                {sorted.map(e=>{
                        return(
                            <h3>{e.name}</h3>
                        )
                    })}
            </div>
        </div>
    )
}

export default Continents
