import React from "react";
import {Link} from 'react-router-dom'
import ContiFunction from './ContiFunction';

const Continents = ({countries})=>{
    let continents = [
        {   name : 'Africa' , 
            img : './imges/africa.jpg', 
            path: '/Africa'
        },
        {   name : 'The Americas' , 
            img : './imges/north-america.jpg', 
            path: '/Americas'
        },
        {   name : 'Asia' , 
            img : './imges/asia.jpg', 
            path: '/Asia'
        },
        {   name : 'Europe' , 
            img : './imges/Europe.jpg', 
            path: '/Europe'
        },
        {   name : 'Australia' , 
            img : './imges/Australia.jpg', 
            path: '/Australia'
        },
        {   name : 'Antarctica - Oceania' , 
            img : './imges/Antarctica.jpg', 
            path: '/Antarctica'
        } ]
        let conti = continents.map(con=>{
            return(
            <div className="card"  
                 onClick={(e)=>{
                            e.preventDefault()
                            countries.filter()
                        }}>
                <Link to= {con.path}><h3>{con.name}</h3>

                </Link>

                <img src={con.img} alt='map' width='200px'/>
            </div>
            )
    })




    return(
        <div className="continents ">
            <div className="cont-card">
                {conti }

            </div>
        </div>
    )
}

export default Continents
