import './css/App.css';
import './css/navigation.css';
import './css/countries-grid.css';
import './css/continents.css';

import React , {useState , useEffect}from "react";
import Home from './component/H'
import Navigation from './component/Navigation';
import CountriesGrid from './component/CountriesGrid';
import Kontinente from './component/Kontinente'
import {BrowserRouter as Router, Routes, Route} from "react-router-dom";


function App() {
    const [countries , setCountries] = useState([])
    let url = 'https://restcountries.com/v2/all';

    useEffect(()=>{
      const fetchData = async ()=>{
        try{
          const response = await fetch (url);
          const data = await response.json();
          console.log(data);

          if(data) setCountries(data)
            if(countries)console.log('countries',countries);
          }catch (error){
          console.log("error", error);
        }
      }
      fetchData()
    } , [])

    const timezone = Intl.DateTimeFormat().resolvedOptions().timeZone;
    let home_inhalt = <div class="card">
                        <div class="card-header bg-transparent">Header</div>
                        <div class="card-body">
                          <h5 class="card-title">Card title</h5>
                          <p class="card-text">Some quick example text to build on the card title and make up the bulk of the card's content.</p>
                        </div>
                        <div class="card-footer bg-transparent">Footer</div>
                      </div>


      return (
    <Router>
      <div className="App my-1 ">
          <header >
            <h1 className='mb-4 text-secondary'>Countries and Regions in the World</h1>
            <Navigation/> 
          </header>

          <div className='container'>
              <Routes>
                  <Route path = '/' 
                        element = {
                        <div id='home-items'>
                        <img  src='/World-Flag-map.jpg' alt='map'/>
                        <p>Your current Timezone : {timezone}</p>
                        </div>
                        }/>


                  <Route path = '/all_countries' 
                        element = 
                        {<div className='countries-container container'>
                        {countries && countries.map(e=>{
                            return(
                        <CountriesGrid key={e.id}
                                        name={e.name}
                                        region={e.region}
                                        capital={e.capital}
                                        flag={e.flag}
                                        languages={e.languages}
                                        nativeName={e.nativeName}
                                        area={e.area}
                                        population={e.population}
                                      />)
                        })
                        } </div>} />


              <Route path = '/kontinente/' 
                     element = {'kontinente'}/>
              <Route path = '/kontinente/:name' 
                     element = {<Kontinente/>}/>

              </Routes>
          </div>
        </div>
    </Router>
  );
}

export default App;
