import './css/App.css';
import './css/navigation.css';
import './css/countries-grid.css';
import './css/continents.css';

import React , {useState , useEffect}from "react";
import Navigation from './component/Navigation';
import CountriesGrid from './component/CountriesGrid';
import Continents from './component/Continents'
import {BrowserRouter as Router, Routes, Route} from "react-router-dom";


function App() {
    const [countries , setCountries] = useState([])
    let url = 'https://restcountries.com/v2/all';

    useEffect(()=>{
      const fetchData = async ()=>{
        try{
          const response = await fetch (url);
          const data = await response.json();
          console.log(data);

          if(data) setCountries(data)
            if(countries)console.log('countries',countries);
          }catch (error){
          console.log("error", error);
        }
      }
      fetchData()
    } , [])

      return (
    <Router>
      <div className="App my-1 ">
          <header >
            <h1 className='mb-4 text-secondary'>Countries and Regions in the World</h1>
            <Navigation/> 
          </header>

          <div className='countries-container'>
              <Routes>
                  <Route path = '/' 
                        element = {<img src='/World-Flag-map.jpg' alt='map' width='250%'/>}/>

                  <Route path = '/all_countries' 
                        element = {countries && countries.map(e=>{
                            return(
                        <CountriesGrid key={e.id}
                                        name={e.name}
                                        region={e.region}
                                        capital={e.capital}
                                        flag={e.flag}
                                        languages={e.languages}
                                        nativeName={e.nativeName}
                                        area={e.area}
                                        population={e.population}
                                      />)
                        })
                        }/> 

              <Route path = '/continents' 
                     element = <Continents  countries={countries}/>/>

              </Routes>
          </div>
        </div>
    </Router>
  );
}

export default App;
