import {Link , useParams} from 'react-router-dom'
import { useState , useEffect } from "react";

const Continent = (countries)=>{

    let {name} = useParams()
    const sortedLands = ()=>{
        countries.filter((land)=>land.region === name )
    }
    const [continent , setContinent  ] = ([])


    useEffect(()=>{
        const url = 'https://restcountries.com/v3/region/'+ name;
        const fetchData = async ()=>{
            try{
                const response = await fetch (url);
                const data = await response.json();
                console.log('contenent',data);
                setContinent(data)
            }catch(error){
                console.log("error", error);
            }
        }
        fetchData()
    } , [name])



    return(
        <div>


        {continent && continent.map((land)=>{
            return(
                <div className="lands-card card">
                  <div className="card-img" >
                        <img className="card-img-top" src={land.flag} alt='land-flag' width={'40%'}/>
                  </div>
                  <div class="card-body ">
                    <h3 className="card-title text-danger">{'üìç '+name}</h3>
                    <ul className="card-ul">
                        <li>Region : <b>{region}</b> </li>
                        <li>Capital : <b>{capital}</b></li>
                        <li>NativeName : <b>{nativeName}</b></li>
                        <li>Area : <b>{area}</b></li>
                        <li>population : <b>{population}</b></li>
                        <li>Languages : <b>{languages.map(e=>(e.name+' '))}</b></li>
                    </ul>
                  </div>
                </div>
            )
        })}

        </div>
    )

}

export default Continent
